import { useState } from "react"
import { axios } from 'axios';
import { Link } from 'react-router-dom';



function Login() {
  
  const [usuario,setUsuario] = useState({email:"",password:""});
  

  async function iniciaSesion(){
    const res = await axios.post("http://localhost:8000/api/session",usuario );
    console.log(res);
}

    return (
      <>
      <div className="col-md-4">
        <form className="px-4 py-3">
            <div className="form-group">
              <label for="exampleDropdownFormEmail1">Email address</label>
              <input value={usuario.email} onChange={(e)=>{setUsuario({...usuario,email:e.target.value})}}
              type="email" className="form-control" id="email"
              placeholder="email@example.com" required></input>

            </div>
            <div className="form-group">
              <label for="exampleDropdownFormPassword1">Password</label>
              <input value={usuario.password} onChange={(e)=>{setUsuario({...usuario,password:e.target.value})}}
              type="password" className="form-control" id="password"
              placeholder="Password"></input>
            </div>
            <button onClick={iniciaSesion} type="button" className="btn btn-primary">Sign in</button>
            <nav>
              <Link to="/about">¿Olvidaste tu contraseña?</Link><br></br>
              
            </nav>
            
        </form>
        <div className="dropdown-divider"></div>
          
        </div>

</>

);
  }

  export default Login;
