import * as React from "react";
//import { useState } from "react";
//import AuthUser from './AuthUser';
import { Link } from 'react-router-dom';
import '../Estilos/login.css';
import { axios } from 'axios';

function Login() {
  //const {http,setToken} = AuthUser();
  //const [email,setEmail] = useState();
  //const [password,setPassword] = useState();
  const [usuario, setUsuario]=React.useState({email:"", password:""});

  //const submitForm = () =>{
      //console.log(email+' '+password);
      //http.post('/login',{email:email,password:password}).then((res)=>{
        //  setToken(res.data.user,res.data.access_token);
     // })
  //}
  async function iniciarSesion(){
    const res = await axios.post("http://localhost:8000/api/iniciar", usuario);
    console.log(res);
  }

    return (
      <>
      <div className="col-md-4" style={{background:"black"}}>     
        <form className="px-4 py-3" style={{background:"black"}}>
            <div className="form-group">
              <h1 style={{color:"orange", textAlign:"center"}}>INICIAR SESION</h1><br/>
              <label style={{color:"white"}}>Email address</label>
              <input value={usuario.email} onChange={(e)=>{setUsuario({...usuario,email:e.target.value})}}
              type="email" className="form-control" id="email"
              placeholder="email@example.com" required></input>

            </div>
            <div className="form-group">
              <label style={{color:"white"}}>Password</label>
              <input value={usuario.password} onChange={(e)=>{setUsuario({...usuario,password:e.target.value})}}
              type="password" className="form-control" id="password"
              placeholder="Password"></input>
            </div>
            <button onClick={iniciarSesion} type="button" className="btn btn-primary">Sign in</button>
            <nav>
              <Link to="/about">¿Olvidaste tu contraseña?</Link><br></br>             
            </nav>
            
        </form>
        <div className="dropdown-divider"></div>
          
        </div>

</>

);
  }

  export default Login;
