import * as React from "react";
import { Routes, Route, Link } from "react-router-dom";
import {Nav} from "react-bootstrap"
import Formjuez from './Formjuez';
import Formjugador from './Formjugador';
import AuthUser from './AuthUser';


function Usuario() {
    const {token,logout} = AuthUser();
    const logoutUser = () => {
        if(token !== undefined){
            logout();
        }
    }

return (
    <div className="container">
        <h1 style={{color:"orange", textAlign:"center"}}>LIGA DE BASKET</h1>
        <header>
            <div className="container">
            <nav className="navbar navbar-expand-lg navbar-light bg-light">
                <Nav.Link as = {Link} to ={"/"}><span>Inicio</span></Nav.Link>
                <div className="collapse navbar-collapse" id="navbarTogglerDemo02">
                    <ul className="navbar-nav me-auto mt-2 mt-lg-0 w-100">
                    <li className="nav-item">
                    <Nav.Link as = {Link} to ={"formjugador"}><span>Formulario Jugador</span></Nav.Link>
                    </li>
                    <li className="nav-item">
                    <Nav.Link as = {Link} to ={"formjuez"}><span>Formulario Juez</span></Nav.Link>
                    </li>
                    <li className="nav-item flex-fill">
                    </li>
                    <li className="nav-item">
                    <span role="button" className="nav-link" onClick={logoutUser}>Logout</span>
                    </li>
                    </ul>                   
                </div>
            </nav>
            </div>
        </header>
    
        <Routes>
        <Route path="formjugador" element={<Formjugador />} />
        <Route path="formjuez" element={<Formjuez />} />
        </Routes>
    </div>
);
}

export default Usuario;
